var popup=document.querySelector(".modal"),openPopupButton=document.querySelector(".modal-form-open"),closePopupButton=document.querySelector(".modal-form-close");openPopupButton.addEventListener("click",function(o){o.preventDefault(),popup.classList.add("modal--show")}),closePopupButton.addEventListener("click",function(){popup.classList.remove("modal--show")}),document.addEventListener("keydown",function(o){27===o.keyCode&&popup.classList.remove("modal--show")});